  transform: translateY(-50%);
  width: 32px;
  height: 48px;
  border-radius: 8px 0 0 8px;
  background-color: #fff;
  border: 1px solid #e9eaec;
}
body.dark .side-bar-cntr::before {
  background-color: #222;
  border-color: transparent;
}
.side-bar-cntr::before, .side-bar-cntr::after {
  content: "";
  transition: 0.24s ease-out;
  cursor: pointer;
  position: fixed;
  top: 50%;
}
.side-bar-cntr:hover::after, .side-bar-cntr:hover::before {
