.video-toolbar .ops .quick-favorite-icon:after {
  content: "î¨";
}
.video-toolbar .ops .quick-favorite .tip,
.video-toolbar .ops .quick-favorite .select-list,
.video-toolbar .ops .quick-favorite .lists-tip {
  line-height: normal;
  position: absolute;
  top: calc(100% + 8px);
  left: 50%;
  transform: translateX(-50%);
  z-index: 1000;
  background: rgba(0,0,0,0.86667);
  padding: 4px 8px;
  border-radius: 4px;
  color: #eee;
  transition: all 0.2s ease-out;
  opacity: 0;
  pointer-events: none;
}
.video-toolbar .ops .quick-favorite .tip.show,
.video-toolbar .ops .quick-favorite .select-list.show,
.video-toolbar .ops .quick-favorite .lists-tip.show {
  opacity: 1;
  pointer-events: initial;
}
.video-toolbar .ops .quick-favorite .tip {
  padding: 8px;
}
.video-toolbar .ops .quick-favorite .lists-tip {
  top: calc(100% + 8px + 42px);
  color: #ccc;
  font-size: 12px;
  z-index: 100;
}
.video-toolbar .ops .quick-favorite .select-list {
  display: flex;
  align-items: center;
}
.video-toolbar .ops .quick-favorite .select-list > * {
  white-space: nowrap;
}
.video-toolbar .ops .quick-favorite .select-list .lists-loading {
  padding: 4px 32px;
}
.video-toolbar .ops .quick-favorite .select-list .lists {
  margin-left: 8px;
