@-moz-document url-prefix("https://zhidao.baidu.com/question"), url-prefix("http://zhidao.baidu.com/question") {
/* 排版 */

#body{
    margin-bottom:50px;
}

a {
    text-decoration: none!important;
}
.search-box-new{
    height:55px;
}

.search-box-new .logo{
  margin-left:-100px !important;
} 

.qb-section{
    width: 1000px;
    margin: auto;
}
.has-menu .search-box-new .search-cont {
    width: 830px!important;

}
.wgt-header-title .wgt-header-title-content {

    width: 960px;

}
.wgt-header-title-btn{
    float:right;
    margin-top:10px;
}

#qb-content {
    padding: 0;
    width: 100%;
    border-radius: 5px;
    box-sizing: border-box;
}
#qb-content>div {
    padding: 0;
    border: none;
    margin: 10px auto;
}
#qb-content>div:first-child {
    margin-top: 0;
}
#qb-content>div:last-child {
    margin-bottom: 0;
}
/* 问答 */

.wgt-ask {
    padding: 10px 20px!important;
}
/* 最好答案 */
div.wgt-best .hd{
    margin-top:0!important;
}
.wgt-best .hd {
    padding: 7px 10px;
}
.wgt-best .bd {
    padding: 0 20px;
}
.wgt-best .wgt-replyer-special-bg {
    border: none;
}
/* 其它回答 */

.wgt-answers {
    padding-bottom: 20px !important;
}
.wgt-answers .hd h2 {
    padding: 10px 20px;
}
.wgt-answers .bd-wrap {
    border: none;
}
.wgt-answers .bd {
    border-bottom: 1px dotted #e9e9e9;
    padding: 24px 20px 0px;
}
.wgt-answers #show-fold-container,
.wgt-answers #show-hide-container,
.wgt-answers .show-answer-dispute {
    height: 40px;
    line-height: 40px;
    margin: 15px 420px 5px;
    border-radius: 60px;
}
.wgt-answers #show-answer-fold,
.wgt-answers #show-answer-hide,
.wgt-answers .show-hide-dispute {
    cursor: pointer;
    font-size: 16px;
}
.wgt-answers #show-answer-fold.hover,
.wgt-answers #show-answer-hide.hover,
.wgt-answers .show-hide-dispute.hover {
    border-radius: 60px;
    text-decoration: none;
}
/* 列表 */

.wgt-related,
.wgt-topic,
.wgt-related .related-list,
.wgt-push {
    padding: 0!important;
}
.wgt-silder-push u1,
.wgt-related .related-list {
    margin: 0;
}
.wgt-topic h2,
.wgt-related h2,
.wgt-push h2 {
    padding: 10px 20px;
}
.wgt-topic ul,
.wgt-related ul,
.wgt-push ul,
.wgt-topic .topic-list,
.wgt-push .push-ul {
    margin: 0px;
    padding: 0px;
}
.wgt-topic ul li,
.wgt-related ul li,
.wgt-push ul li {
    padding: 5px 20px;
}
.wgt-topic ul li:hover a,
.wgt-related ul li:hover span,
.wgt-push ul li:hover span,
.wgt-push ul li:hover a {
    font-weight: bold;
}
/* 调节 */

#qb-content .wgt-push,
#qb-content .push-ul,
#qb-content .wgt-related,
#qb-content .related-list,
#qb-content .wgt-topic,
#qb-content .topic-list {
    margin-top: 0;
    padding-top: 0;
}
#qb-content .wgt-push,
#qb-content .push-ul {
    max-height: 500px;
}

.wgt-best-operator{
    padding-bottom:10px;
}


.f-aid,
a.f-aid {
    color: #333;
}
body .pager a:hover {
    color: #fff;
    border: solid 1px #43cc69;
}


/* 修复排版错位 */
.accuse-area .wgt-accuse {

    line-height: 1;

}

 .wgt-replyer-all-follow{margin-right:40px;}

 .nav-menu-container{margin-top:20px;}

.wgt-ask .ask-info .wgt-accuse {

    top: 9px;

}
.wgt-replyer-all {
    margin-left: 20px;
}
}

